package week1.day1;

public class NegativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = -20;

		if (number >= 0) {
			System.out.println("Given Number is " + number + " . Hence it a positive number by default");
		} else {
			System.out.println(-number);
		}

	}

}
